INSERT INTO `projects` VALUES ('38', 'Music Voice SMS', '<p>(Catcom) Music Voice SMS is a unique messaging product that will allow your customers to personalize their voice messages with songs, making the message exciting and communicative by enabling them to package their personal style and emotion in their message. It can be used to send greetings with music to friends and family, subsidizing the voice messages by adding audio ads or for enterprise adverts.</p>\r\n', 'music-voice-sms', '/uploads/project/', 'catcom-music-voice.jpg', '16521', 'vi', '2016-05-17 03:15:25', '2016-05-17 03:15:25');
