INSERT INTO `projects` VALUES ('44', 'Content Managemens System', '<p>(Catcom) With the rapid mass market proliferation of mobile subscribers and the increasing use of mobile data, especially video, Mobile operators require a strong content management system that can supportthe entire range of content types and deliver it across multiple channels like SMS, MMS, USSD, WAP, Voice/IVR and the mobile internet.</p>\r\n\r\n<p>TelcoVAS&rsquo;s Content Management System (CMS) is a flexible and scalable platform that enablesmulti channel deployment of content based value added services, provisioning and billing control of various content providers (like pay per download, pay per access, subscription packs, bundles) and a powerful content adaptation engine across different media and devices.</p>\r\n', 'content-managemens-system', '/uploads/project/', 'catcom-content-system.png', '75470', 'vi', '2016-05-17 03:42:20', '2016-05-17 03:42:20');
