INSERT INTO `projects` VALUES ('52', 'Pay4SMS and GiftSMS', '<p>(Catcom) GiftSMS enables a mobile subscriber to gift a SMS reply to his friend or colleague by just inserting a word &ldquo;gift&rdquo; at the start of the message. Our SMS router sends the SMS to receiver with the message &ldquo;the response to this SMS is gifted by subscriber number +6522355666&rdquo;. Pay4SMS enables the receiver to pay for the SMS eg. Father pays for son&rsquo;s SMS &ndash; Pay4ME is for collect calls while Pay4SMS is for collect SMS.</p>\r\n', 'pay4sms-and-giftsms', '/uploads/project/', 'catcom-paysms.jpg', '57826', 'vi', '2016-05-17 03:58:26', '2016-05-17 03:58:26');
